
public class Calculations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number1 = 22;
		int number2 = 37;
		int number3 = 88;
		double number4 = 21.058;
		
		double calc1 = number1 + number2 + number3 + number4;
		double calc2 = calc1 / 3;
		double calc3 = calc2 * 2;
		double calc4 = calc3 - 9;
		
		System.out.println("Starting numbers = " + number1 + "," + number2 + "," + number3 + "," + number4);
		System.out.println("Calculation 1 = " + calc1);
		System.out.println("Calculation 2 = " + calc2);
		System.out.println("Calculation 3 = " + calc3);
		System.out.println("Calculation 4 = " + calc4);
		
		
	}

}
